import React from 'react'

function Categories({value,onClickCateg}) {
  const [activeIndex, setActiveIndex] = React.useState(0)
  const categoris = ['Все', 'Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые']
  const activeClick = (sum) => {
    setActiveIndex(sum)
  }

  return (
    <div className="categories">
      <ul>
        {
          categoris.map((value, index) => (
            <li key={value} onClick={() => activeClick(index)} className={activeIndex == index ? 'active' : ''}>{value}</li>
          ))
        }
      </ul>
    </div>
  )
}


export default Categories
