import React from 'react'

function Categories() {
  const [activeIndex, setActiveIndex] = React.useState(2)
  const categoris = ['Все','Мясные','Вегетарианская','Гриль','Острые','Закрытые' ]
  const activeClick = (sum) => {
    setActiveIndex(sum)
  }

  return (
    <div className="categories">
      <ul>
        <li onClick={() => activeClick(0)} className={activeIndex == 0 ? "active" : ""}>Все</li>
        <li onClick={() => activeClick(1)} className={activeIndex == 1 ? "active" : ""}>Мясные</li>
        <li onClick={() => activeClick(2)} className={activeIndex == 2 ? "active" : ""}>Вегетарианская</li>
        <li onClick={() => activeClick(3)} className={activeIndex == 3 ? "active" : ""}>Гриль</li>
        <li onClick={() => activeClick(4)} className={activeIndex == 4 ? "active" : ""}>Острые</li>
        <li onClick={() => activeClick(5)} className={activeIndex == 5 ? "active" : ""}>Закрытые</li>
      </ul>
    </div>
  )
}


export default Categories
