import axios from 'axios';
import React from 'react';
import { useParams } from 'react-router-dom';

const FullPizza = () => {
  const { id } = useParams();
  const [pizza, setPizza] = React.useState;
  React.useEffect(() => {
    async function fetchPizza() {
      try {
        const { data } = await axios.get('https://63d81502afbba6b7c94a4d2b.mockapi.io/items/' + id);
        setPizza(data);
      } catch (error) {
        alert('Ошибка при получении пиццы');
      }
    }
  }, [id]);

  return (
    <div className="container">
      <img src="" />
      <h2>{id}</h2>
      <p>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Nam eligendi ea aliquam accusamus
        amet tempora culpa incidunt quae, illum commodi, perferendis asperiores quod debitis alias
        qui repudiandae quam. Eligendi, velit?
      </p>
      <h4>350 ₸</h4>
    </div>
  );
};

export default FullPizza;
